myApp.controller("MathController",["$scope",function(e){e.resetMainForm=function(){e.x="",e.y="",e.z=""},e.addition=function(e,n){return Number(e)+Number(n)},e.addMultiple=function(){if(e.xx){var n=0;return $.each(e.xx.split(" "),function(){n+=Number(this)}),n}return"Answer"},e.subtraction=function(e,n){return Number(e)-Number(n)},e.remainingBudget=function(){if(e.expenses){var n=e.budget||0;return $.each(e.expenses.split(" "),function(){n-=Number(this)}),n}return"How Much is Left"},e.resetBudget=function(){e.expenses="",e.budget=""},e.multiply=function(e,n){return Number(e)*Number(n)},e.divide=function(e,n){var r=Number(e)/Number(n);return r?r%1!==0?parseFloat(Math.round(100*r)/100).toFixed(2):r%1===0?r:void 0:""}}]);